        <script src="https://cdn.jsdelivr.net/npm/chart.js@4.4.1/dist/chart.umd.min.js"></script>
        <script>
            // Chart.js context
            const ctx = document.getElementById('salesChart').getContext('2d');

            // Fake datasets (replace with backend data if needed)
            const salesData = {
                7: {
                    labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
                    data: [240, 310, 190, 450, 380, 520, 610]
                },
                30: {
                    labels: Array.from({
                        length: 30
                    }, (_, i) => `Day ${i + 1}`),
                    data: Array.from({
                        length: 30
                    }, () => Math.floor(Math.random() * 600 + 100))
                },
                90: {
                    labels: Array.from({
                        length: 90
                    }, (_, i) => `Day ${i + 1}`),
                    data: Array.from({
                        length: 90
                    }, () => Math.floor(Math.random() * 600 + 100))
                }
            };

            // Initial dataset (7D)
            let currentRange = 7;

            const salesChart = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: salesData[currentRange].labels,
                    datasets: [{
                        label: 'Sales (₹)',
                        data: salesData[currentRange].data,
                        fill: true,
                        borderColor: 'rgb(54, 162, 235)',
                        backgroundColor: 'rgba(54, 162, 235, 0.2)',
                        tension: 0.3,
                        pointRadius: 4,
                        pointBackgroundColor: 'rgb(54, 162, 235)',
                    }]
                },
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                        y: {
                            beginAtZero: true
                        }
                    }
                }
            });

            // Range switch buttons
            document.querySelectorAll('[data-range]').forEach(btn => {
                btn.addEventListener('click', function () {
                    document.querySelectorAll('[data-range]').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentRange = this.dataset.range;

                    salesChart.data.labels = salesData[currentRange].labels;
                    salesChart.data.datasets[0].data = salesData[currentRange].data;
                    salesChart.update();
                });
            });
        </script>

